import { Box, Container, VStack, Heading, Text, SimpleGrid, Button } from '@chakra-ui/react';

export default function Home() {
  const featuredCourses = [
    {
      title: "Curso Open Water",
      description: "Primer nivel de buceo recreativo",
    },
    {
      title: "Curso Advanced",
      description: "Mejora tus habilidades de buceo",
    },
    {
      title: "Bautismo de Buceo",
      description: "Primera experiencia bajo el agua",
    },
  ];

  return (
    <Box>
      {/* Hero Section */}
      <Box 
        bg="blue.500" 
        color="white" 
        py={32} 
        mt="64px" // Espacio para el navbar fijo
      >
        <Container maxW="container.xl">
          <VStack spacing={6} align="flex-start">
            <Heading size="2xl">Descubre el Mundo Submarino</Heading>
            <Text fontSize="xl">
              Aprende a bucear con los mejores profesionales
            </Text>
            <Button colorScheme="white" variant="outline">
              Ver Cursos
            </Button>
          </VStack>
        </Container>
      </Box>

      {/* Cursos Destacados */}
      <Container maxW="container.xl" py={16}>
        <VStack spacing={12}>
          <Heading as="h2" size="xl">
            Cursos Destacados
          </Heading>
          <SimpleGrid columns={{ base: 1, md: 3 }} spacing={10} width="100%">
            {featuredCourses.map((course, index) => (
              <Box 
                key={index} 
                p={6} 
                boxShadow="md" 
                borderRadius="lg"
                bg="white"
              >
                <VStack align="flex-start" spacing={3}>
                  <Heading size="md">{course.title}</Heading>
                  <Text>{course.description}</Text>
                  <Button colorScheme="blue" variant="link">
                    Más información →
                  </Button>
                </VStack>
              </Box>
            ))}
          </SimpleGrid>
        </VStack>
      </Container>
    </Box>
  );
}
