import { raf, getDocument } from '@zag-js/dom-query';
import { createFocusTrap } from 'focus-trap';
export { createFocusTrap } from 'focus-trap';

// src/index.ts
function trapFocus(el, options = {}) {
  let trap;
  raf(() => {
    const contentEl = typeof el === "function" ? el() : el;
    if (!contentEl) return;
    trap = createFocusTrap(contentEl, {
      escapeDeactivates: false,
      allowOutsideClick: true,
      preventScroll: true,
      returnFocusOnDeactivate: true,
      delayInitialFocus: false,
      fallbackFocus: contentEl,
      ...options,
      document: getDocument(contentEl)
    });
    try {
      trap.activate();
    } catch {
    }
  });
  return function destroy() {
    trap?.deactivate();
  };
}

export { trapFocus };
